import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../AuthContext';
import { FaHome, FaSignInAlt, FaUserPlus, FaSignOutAlt } from 'react-icons/fa';

const Navbar = () => {
  const { isLoggedIn, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();  // Call logout from context
    navigate('/');
  };

  return (
    <nav className="bg-blue-600 p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-white text-lg font-bold flex items-center">
          <FaHome className="mr-2" /> Home
        </Link>
        <div>
          {isLoggedIn ? (
            <button
              onClick={handleLogout}
              className="text-white mr-6 flex items-center hover:text-gray-200 transition duration-300"
            >
              <FaSignOutAlt className="mr-1" /> Logout
            </button>
          ) : (
            <>
              <Link to="/login" className="text-white mr-6 flex items-center hover:text-gray-200 transition duration-300">
                <FaSignInAlt className="mr-1" /> Login
              </Link>
              <Link to="/register" className="text-white flex items-center hover:text-gray-200 transition duration-300">
                <FaUserPlus className="mr-1" /> Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
